a=50;b=0.2;c=0.1;d=0.8;e=4;Y=4400;w=5;A=[1 b; 1 -d];h=[a+c*Y; -e*w];QP=inverse(A)*hPG=[-50:50:1050];Qd=a+c*Y-b*PG;Qs=-e*w+d*PG;plot(PG, Qd);holdplot(PG, Qs, "r");xlabel("P", "fontsize", 14, "fontweight", "bold");ylabel("Q", "fontsize", 14, "fontweight", "bold");holdY=3000;h=[a+c*Y; -e*w];QP=inverse(A)*h%PG=[-50:50:1050];Qd2=a+c*Y-b*PG;%Qs2=-e*w+d*PG;holdpause(1)plot(PG, Qd2);%plot(PG, Qs2, "y");%xlabel("P", "fontsize", 14, "fontweight", "bold");%ylabel("Q", "fontsize", 14, "fontweight", "bold");